window.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector(".intro__video"),t=document.querySelector(".header"),n=e.clientHeight-t.clientHeight;window.addEventListener("resize",()=>{document.querySelector(".intro").style.height=n+"px"});let i=document.querySelector(".show-map"),c=document.querySelector(".callback");i.addEventListener("click",e=>{e.preventDefault(),c.classList.toggle("active"),c.classList.contains("active")?i.innerHTML="Скрыть карту":i.innerHTML="Показать карту"})});